import java.util.*;
public class PE_5_18_A {

	public static void main(String[] args) {
		
		System.out.println("Pattern A");
		
		int array = 1;
		while ( array <= 6)
		{
			int i = 1;
			while ( i <= array)
			{
				System.out.print(" "+ i);
				i ++;
			}
			System.out.println("");
			array ++;
		}
		
		
		// TODO Auto-generated method stub

	}

}
