
public class PE_5_18_B {

	public static void main(String[] args) {
		
		System.out.println( " Pattern B");
		
		int arr = 1;
		while( arr<= 6)
		{
			int i = 1;
			while (  i<= 7 - arr)
			{
				System.out.print(" " + i);
				i ++ ;
			}
			System.out.println("");
			arr ++;
			
		}
		// TODO Auto-generated method stub

	}

}
